import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { Grid } from "@material-ui/core";
import { Card } from "@material-ui/core";
import { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import { useSelector } from "react-redux";
// test
import labelmake from "labelmake";

const OrderFinish = () => {
  const [ receiptPDF, setReceiptPDF ] = useState('')
  const state = useSelector(state => state);
  const history = useHistory()
  const handleLink = path => history.push(path)

  // 領収書PDF
  const MakeReceipt = async () => {

    const template = {
      basePdf: { width: 210, height: 297 },
      schemas: [
        {
          field1: {
            position: { x: 20, y: 20 },
            width: 50,
            height: 50,
            fontSize: 30,
            type: "text"
          }
        }
      ]
    };
    const inputs = [
      { field1: "aa", field2: "aaaaaaaaaaaa" },    
    ];
    const pdf = await labelmake({ template, inputs });
    const blob = new Blob([pdf.buffer], { type: "application/pdf" }); 
    console.log(URL.createObjectURL(blob))
    setReceiptPDF(URL.createObjectURL(blob))

  }

  useEffect(()=>{
    MakeReceipt()
  },[])

  useEffect(()=>{
    if(!state.user.uid){
      // handleLink('/') 
    }
  },[state.user])

    return (
        <div>
          <Grid container alignItems="center" >
             <Grid item xs={8}>
               <Card>
                <h1>決済が完了しました！</h1>
                <p>この度はご注文ありがとうございます。</p>
                {receiptPDF !== '' && <a href={receiptPDF}>領収書をダウンロード</a>}
                    <Button
                            variant="contained"
                            color="primary"
                            component={Link}
                            to="/"
                            >
                            トップ画面を表示する
                    </Button>
                 </Card>
             </Grid>   
          </Grid>
        </div>        
    )
}

export default OrderFinish
