import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { Grid } from "@material-ui/core";
import { Card } from "@material-ui/core";
import { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import { useSelector } from "react-redux";
// test
import labelmake from "labelmake";
import { Email } from "../module/smtp"

const OrderFinish = () => {
  const [ receiptPDF, setReceiptPDF ] = useState('')
  const state = useSelector(state => state);
  const history = useHistory()
  const handleLink = path => history.push(path)

  // 購入確認メール
  const sendMeil = () => {
    Email.send({
      Host : "smtp.yourisp.com",
      Username : "massurumatsuyama@gmail.com",
      Password : "FEDE3004834FF9BD93BB2E0B608BAC824058",
      To : 'kirito.shimura@rakus-partners.co.jp',
      From : "massurumatsuyama@gmail.com",
      Subject : "This is the subject",
      Body : "And this is the body"
    }).then(
      message => alert(message)
    )
  }

  // 領収書PDF
  const MakeReceipt =  async () => {
    // You can also use Uint8Array or ArrayBuffer as a basePdf
    // const basePdf = fs.readFileSync("path/to/your.pdf")
    // const basePdf = await fetch("path/to/your.pdf").then((res) => res.arrayBuffer());
    const template = {
    "schemas": [
      {
        "field1": {
          "type": "text",
          "position": {
            "x": 20,
            "y": 20
          },
          "width": 150,
          "height": 15,
          "alignment": "center",
          "fontSize": 30,
          "characterSpacing": 0,
          "lineHeight": 1
        },
        "field2": {
          "type": "text",
          "position": {
            "x": 20,
            "y": 35
          },
          "width": 150,
          "height": 10,
          "alignment": "center",
          "fontSize": 20,
          "characterSpacing": 0,
          "lineHeight": 1
        },
        "field3": {
          "type": "text",
          "position": {
            "x": 20,
            "y": 45
          },
          "width": 150,
          "height": 200,
          "alignment": "left",
          "fontSize": 12,
          "characterSpacing": 0,
          "lineHeight": 1
          }
        }
      ]
    }
    const inputs = [{"field1":"領収書","field2":"決済内容","field3":"text"}];
    const pdf = await labelmake({ template, inputs });    

    // Browser
    const blob = new Blob([pdf.buffer], { type: "application/pdf" });    
    console.log(URL.createObjectURL(blob))
    setReceiptPDF(URL.createObjectURL(blob))

  }

  useEffect(()=>{
    // MakeReceipt()
  },[])

  useEffect(()=>{
    if(!state.user.uid){
      // handleLink('/') 
    }
  },[state.user])

    return (
        <div>
          <Grid container alignItems="center" >
             <Grid item xs={8}>
               <Card>
                <h1>決済が完了しました！</h1>
                <p>この度はご注文ありがとうございます。</p>
                {receiptPDF !== '' && <p><a href={receiptPDF}>領収書を表示</a></p>}
                    <Button
                            variant="contained"
                            color="primary"
                            component={Link}
                            to="/"
                            >
                            トップ画面を表示する
                    </Button>
                 </Card>
             </Grid>   
          </Grid>
        </div>        
    )
}

export default OrderFinish
