import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { Grid } from "@material-ui/core";
import { Card } from "@material-ui/core";
import { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import { useSelector } from "react-redux";
// test

const Administrator = () => {  
  const state = useSelector(state => state);
  const history = useHistory()
  const handleLink = path => history.push(path)

  const [items, setItems] = useState([])
  const [selectItem, setSelectItem] = useState('')

  const check = (e) => {
      console.log(e.target.value);
      setSelectItem(e.target.value)
  }

  useEffect(()=>{
      setItems(state.Coffee)
  },[state.Coffee])

  useEffect(()=>{
    if(state.user.uid !== "oD7bRul05kcE0Ez2oQfH01PKv073"){
      handleLink('/') 
    }
  },[state.user])

    return (
        <div>
          <Grid container alignItems="center" >
             <Grid item xs={8}>
               <Card>
                    <h1>管理者画面</h1>
                    <select name="items" id="item-select" value={selectItem} onChange={check}>        
                        {items.map( c => {
                            return ( <option key={c.id} value={c.id}>{c.name}M:{c.priceM}円、L:{c.priceL}円</option> )
                        })}
                    </select>      
                </Card>
             </Grid>   
          </Grid>
        </div>        
    )
}

export default Administrator
