import firebase from "firebase/compat/app";

var firebaseConfig = {
    apiKey: "AIzaSyDx97yiY2400qInRktsZ-6DmnBwN1Sig2w",
    authDomain: "coffee-app-782e3.firebaseapp.com",
    projectId: "coffee-app-782e3",
    storageBucket: "coffee-app-782e3.appspot.com",
    messagingSenderId: "301472667564",
    appId: "1:301472667564:web:e35347de2e59ed12c44c09",
    measurementId: "G-WW461MQNP1"
  };

  // Initialize Firebase
  firebase.initializeApp(firebaseConfig);

const reducer = (state = initialState, action) => {
    switch (action.type) {
      case 'UPDATE_CART':            
        return { ...state,
                  cart: action.payload.cart,                  
        };  
      case 'UPDATE_CARTITEMLIST':            
        return { ...state,
                  cart: { ...state.cart, cartItemList: action.payload.cartItemList }
        };  
      case 'ADD_ORDERHISTORY':            
        return { ...state,
                  orderhistory: [...state.orderhistory, action.payload.orderhistory]                  
        }; 
      case 'CREATE_CART' :
        return {
            ...state,
            cart: action.payload.cart            
        }
      case 'UPDATE_USER' :        
        return {
          ...state,
          user: action.payload.user
        }
      case 'FETCH_CART' :        
        return {
            ...state,
            cart: action.payload.cart
        }
      case 'FETCH_ORDERHISTORY' :        
        return {
            ...state,
            orderhistory: [ ...state.orderhistory, action.payload.orderhistory ]
        }
      case 'FETCH_ITEM' :
        if(action.payload.Items.Coffee) return { ...state,Coffee: action.payload.Items.Coffee }
        if(action.payload.Items.Topping) return { ...state,Topping: action.payload.Items.Topping }
      default:
        return state;
    }
  };

  export const newCart = { 
    addNumber:'',
    address:'',
    cartItemList:[],                                                
