// import React, { Component } from 'react';
import firebase from "firebase/compat/app";
import 'firebase/compat/firestore';

import Button from '@material-ui/core/Button' 

import React, { useState } from 'react';
import {useDispatch,useSelector} from "react-redux"
import { GetItemById } from '../components/Items'
import { useParams } from 'react-router-dom'
import ItemView from './ItemView'
// import { useEffect } from 'react';
import {    
    Switch,
    Route,
    Link
  } from 'react-router-dom'
  import Cart from './Cart'

export const ItemDetail = () =>{

const state = useSelector(state => state)
const dispatch = useDispatch()
const {Item_id} = useParams() 

let [choiceTopping, setChoiceTopping] = useState([])
let [sizeChoice,setSizeChoice] = useState("")
let [quantityChoise,setQuantityChoise] = useState("")
let [sizeFinal,setSizeFinal] = useState("")
let [puantityFinal,setQuantityFinal] = useState("")


const setTopping = (e) => {
    const getId = e.target.value * 1

    if(choiceTopping.includes(getId)){
        setChoiceTopping(choiceTopping.filter(item => item !== getId));
    } else {
        setChoiceTopping([...choiceTopping, getId]);
    }
}
    let totalPrice = ()  => {
        if(sizeChoice === "M"){
            return  choiceTopping.length * GetItemById(Item_id*1,state.Topping).priceM * quantityChoise + GetItemById(Item_id*1,state.Coffee).priceM * quantityChoise           
        }else if(sizeChoice === "L"){
            return  choiceTopping.length * GetItemById(Item_id*1,state.Topping).priceL * quantityChoise + GetItemById(Item_id*1,state.Coffee).priceL * quantityChoise 
        }
    }
    let setQuantity = (e) => setQuantityChoise(e.target.value) 

    let allSelects ={}
    let sizePrice = ""

    const checkValid = ()  => {
        setSizeFinal("")
        setQuantityFinal("")

        if (sizeChoice==="") {       
           setSizeFinal("サイズを入力してください") 
        } 
        if (quantityChoise==="" || quantityChoise==="個数を選択してください") {       
           setQuantityFinal("個数を選択してください")
        }else{
            if(sizeChoice === "M"){
                sizePrice= "priceM"
            }else if(sizeChoice === "L"){
                sizePrice= "priceL"
            }

            allSelects = {number:quantityChoise, Itemid:GetItemById(Item_id*1,state.Coffee).id, toppingid:choiceTopping, price:sizePrice}            
            // カート作成         
            let cartItemList = state.cart.cartItemList            
            cartItemList.push(allSelects)            
            let cartItem = state.cart
            cartItem.cartItemList = cartItemList

            // ログインしていた場合はFirebase、storeを更新
            if(state.user && state.user.uid){
                firebase.firestore()
                  .collection(`users/${state.user.uid}/carts`)
                  .doc(state.cart.uid)
                  .update(cartItem)
                  .then( querySnapshot => {                                              
                    dispatch({ type: 'UPDATE_CARTITEMLIST', payload: { cartItemList }})            
                  })

            // ログインしていない場合はstore飲み更新
            }else{
                dispatch({ type: 'UPDATE_CARTITEMLIST', payload: { cartItemList }})
            }                
        }                
      }

    const mainview = {
        display:"flex"
    }

    const itemdetail = {
        display: "flexbox",
        width: "50%"
    }

    const itemview = {
        display: "flexbox",
        width: "50%"
    }

    return (
        <div style={mainview}>
            {/* <div style={itemview}><ItemView /></div> */}
            <div style={itemdetail}>
            <h1>商品詳細</h1>
            <h2>{GetItemById(Item_id*1,state.Coffee).name} </h2>            
            <img src={`${process.env.PUBLIC_URL}/${GetItemById(Item_id*1,state.Coffee).image}`}  height="400px" alt="商品"/>                    
            <p>{GetItemById(Item_id*1,state.Coffee).detail}</p>
            <div>
               <input type="radio" name="size" id='M' onClick={() => setSizeChoice("M")} value="M" /><label for='M'>
               <span>&nbsp;M&nbsp;&nbsp;{GetItemById(Item_id*1,state.Coffee).priceM}(税抜)</span></label>
               <input type="radio" name="size" id='L' onClick={() => setSizeChoice("L")} value="L" /><label for='L'>
               <span>&nbsp;L&nbsp;&nbsp;{GetItemById(Item_id*1,state.Coffee).priceL}(税抜)</span></label>
            </div>
            <p>{sizeFinal}</p>
            <div>
                <select onChange={setQuantity}>
                    <option >---個数を選択してください---</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="6">6</option>
                    <option value="7">7</option>
                    <option value="8">8</option>
                    <option value="9">9</option>
                    <option value="10">10</option>
                </select>
                <p>{puantityFinal}</p>            
            </div>
            <div>
                <p>トッピングを選択できます。１つにつきMサイズ：200円・Lサイズ：300円</p>
                    {state.Topping && state.Topping.map((row,index) => (
                        <div key={index}>                                                       
                            <input id={"tid" + index} type="checkbox" value={row.id} onChange={setTopping} checked={choiceTopping.includes(row.id)}/>
                            <label for={"tid" + index}>{GetItemById(row.id,state.Topping).name}</label>                            
                        </div>
                        ))
                    }                                            
                合計金額:{totalPrice()}(税抜)
            </div>
                <Button variant="contained" color="primary" onClick={checkValid}><Link to= '/cart'>カートに追加</Link></Button>
                <Switch><Route path = '/cart' exact component = {Cart}></Route></Switch>
            </div>
        </div>
    )
}
export default ItemDetail
