    // const basePdf = fs.readFileSync("path/to/your.pdf")
    // const basePdf = await fetch("path/to/your.pdf").then((res) => res.arrayBuffer());
    const template = {
    "schemas": [
      {
        "field1": {
          "type": "text",
          "position": {
            "x": 20,
            "y": 20
          },
          "width": 150,
          "height": 15,
          "alignment": "center",
          "fontSize": 30,
