import React, {useState} from 'react'
import Button from '@material-ui/core/Button';
import SearchSharp from '@material-ui/icons/SearchSharp';
import BackspaceSharpIcon from '@material-ui/icons/BackspaceSharp';
import { useSelector } from 'react-redux';
// import { GetItemById } from '../components/Items';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import TablePagination from '@material-ui/core/TablePagination';
import Paper from '@material-ui/core/Paper';
import '../App.css';

import { Switch, Route, Link } from 'react-router-dom';
import { useEffect } from 'react';
// import RactDom from 'react-dom'

const ItemView =()=>{
    const state = useSelector(state => state)
    const [word, SetWord]=useState('')
    const type=(e)=>{SetWord(e.target.value)}    

    const [search, setSearch] = useState([])

    const Kensaku=()=>{
        const searched=state.Coffee.filter(val2 =>{
            return val2.name.includes(word)
        });
        setSearch(searched)
    }    

    const Kuria=()=>{
        const word=document.getElementById('box');
        word.value=''
    }
    useEffect(()=>{  
        console.log('check');      
        setSearch(state.Coffee)
    },[state.Coffee])



    return(
    <Paper>
        <TableContainer>            
            <Table stickyHeader aria-label="sticky table">
                <TableHead>
                    <TableRow>
                        <TableCell>
                            <h3>商品を検索する</h3>                        
                            <input type='text' value={word} onChange={type}　id="box" />                        
                            <Button variant="contained" color="primary" type='submit' onClick={Kensaku}>検索<SearchSharp /></Button>
                            <Button variant="contained" color="primary" type='reset' onClick={Kuria}>クリア<BackspaceSharpIcon /></Button>
                        </TableCell>
                    </TableRow>                                
                </TableHead>
                <TableBody>
                    {search.map((val3,index)=>{
                        return(
                            <TableRow key={index} hover role="checkbox" tabIndex={-1}>
                                <nav key={index}>
                                    <Grid container justifyContent="center">
                                        <Grid item xs={12} sm={6}>
                                            <Table className="container" >
                                                <TableHead>
                                                    <TableRow>
                                                        <TableCell><Link to ={'/ItemDetail/'+val3.id}>{val3.name}</Link></TableCell>
                                                    </TableRow>
                                                </TableHead>
                                                <TableBody>
                                                    <TableRow>
                                                        <TableCell>
                                                            <Link to ={'/ItemDetail/'+val3.id}><img src={`${process.env.PUBLIC_URL}/${val3.image}`} width="100%" alt="商品" /></Link>
                                                        </TableCell>
                                                        <TableCell> 
                                                            <p>【M】{val3.priceM}円(税抜)</p>
                                                            <p>【L】{val3.priceL}円(税抜)</p>
                                                        </TableCell>
                                                    </TableRow>
                                                </TableBody>
                                            </Table>
                                        </Grid>
                                    </Grid>
                                </nav>  
                            </TableRow>                                       
                        )
                    })}
                </TableBody>                                    
            </Table>
        </TableContainer>
        <TablePagination
            rowsPerPageOptions={[10, 25, 100]}
            component="div"
            count={1}
            rowsPerPage={30}
            page={1}            
        />
    </Paper>
    )}


export default ItemView
