import './App.css';
import { newCart } from './store/index';
// firebase
import firebase from "firebase/compat/app";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import "firebase/compat/auth";
import 'firebase/compat/firestore';
// react
import { 
  Switch,
  Route,  
  useHistory
  // useParams,  
} from 'react-router-dom'
import Cart from './views/Cart'
import ItemDetail from './views/ItemDetail'
import ItemView from './views/ItemView'
import OrderConfirm from './views/OrderConfirm'
import OrderFinish from './views/OrderFinish'
import OrderHistory from './views/OrderHistory'
import './store/index'

// material ui
import Button from '@material-ui/core/Button';
import { useDispatch, useSelector } from "react-redux";
import React, { useEffect } from "react";
import { AppBar, Toolbar } from "@material-ui/core";
// material ui icon
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import HistoryIcon from '@material-ui/icons/History';
import CheckIcon from '@material-ui/icons/Check';

function HistoryApp(props){
  const history = useHistory();
  const handleLink = path => history.push(path)
  let newDOM = ''

  if(props.tag === 'img') newDOM = ( <img src={props.context} alt="ロゴ" onClick={() => handleLink(props.link)} > </img> )
  if(props.tag === 'button') newDOM = ( <Button onClick={() => handleLink(props.link)}>{props.icon}{props.name}</Button> )
  if(props.tag === 'button_img') newDOM = ( <Button onClick={() => handleLink(props.link)}><img src={props.context} alt="ロゴ" ></img></Button> )

  return (
    <>
    {newDOM}
    </>
  )
}
const MakeReceipt = async () => {
  // 領収書PDF
  const template = {
    basePdf: { width: 210, height: 297 },
    schemas: [
      {
        field1: {
          position: { x: 20, y: 20 },
          width: 50,
          height: 50,
          fontSize: 30,
          type: "text"
        }
      }
    ]
  };
  const inputs = [
    { field1: "aa", field2: "aaaaaaaaaaaa" },    
  ];
  const pdf = await labelmake({ template, inputs });
  const blob = new Blob([pdf.buffer], { type: "application/pdf" });  
  return ( <a src={URL.createObjectURL(blob)}>download</a>)
}

function App(props) {
  const dispatch = useDispatch();
  const state = useSelector(state => state);
  const history = useHistory();
  const handleLink = path => history.push(path)     

  const login = () => {        
    const google_auth_provider = new firebase.auth.GoogleAuthProvider();        
    firebase.auth().signInWithRedirect(google_auth_provider)                                   
  } 

  const logout = () => {                
    firebase.auth().signOut().then( () => {        
      dispatch({ type: 'UPDATE_USER', payload: { user: {}}})
      handleLink('/')
    })        
  }

  const check = () => {                    
    console.log(state);       
  }

  const fetchItems = () => {    
    firebase.firestore()
            .collection(`Items`)
            .get()
            .then( querySnapshot => {
              querySnapshot.forEach( doc => {                                
                dispatch({ type: 'FETCH_ITEM', payload: { Items: doc.data() }})                  
              })                        
            })    
  }

  const fetchCarts = async () => {         
    const setCart = Object.assign({},newCart)
    let cartUid = ''

    // 途中ログインの場合
    if(localStorage.getItem('middle_login')){      
      const newCartItemList = JSON.parse(localStorage.getItem('cart')).cartItemList      
      setCart.cartItemList = newCartItemList            
    }

    await firebase.firestore()
            .collection(`users/${state.user.uid}/carts`)
            .get()
            .then( querySnapshot => {                            
              if(querySnapshot.empty){
                  createCarts(setCart);
              }else{
                querySnapshot.forEach( doc => {
                  let cartMergeUid = doc.data()
                  cartMergeUid.uid = doc.id
                  cartUid = doc.id
                  if(doc.data().status === 0) dispatch({ type: 'FETCH_CART', payload: { cart: cartMergeUid }}) 
                  if(doc.data().status !== 0) dispatch({ type: 'FETCH_ORDERHISTORY', payload: { orderhistory: doc.data(), uid: doc.id }})
                })                        
              }              
            })

      //　途中ログイン処理
      if(localStorage.getItem('middle_login')){
        localStorage.removeItem('middle_login');
        updateCart(setCart, cartUid)
        handleLink('/orderconfirm')
      }                     
    }      

  const start = () => {       
    const auth = getAuth(); 
    onAuthStateChanged(auth, ( user => {      
      if(user){                 
        dispatch({ type: 'UPDATE_USER', payload: { user }})
      }                         
    }))
  }

  const updateCart = (setCart, cuid) => {    
    const newCarts = Object.assign({},setCart)
    newCarts.uid = cuid
    firebase.firestore()
            .collection(`users/${state.user.uid}/carts`)
            .doc(cuid)
            .update(setCart)
            .then( querySnapshot => {                                                            
              dispatch({ type: 'UPDATE_CART', payload: { cart: newCarts }})              
            })   
  }

  const createCarts = (createCart) => {    
    firebase.firestore()
            .collection(`users/${state.user.uid}/carts`)                     
            .add(createCart)          
            .then( d => {
              const newCarts = Object.assign({},createCart)
              newCarts.uid = d.id
              dispatch({ type:'CREATE_CART', payload:{ cart: newCarts }})                 
            }) 
  } 

  useEffect(()=>{
    if(state.user && state.user.uid){                  
      fetchCarts();
      console.log("user uid = " + state.user.uid);
    }  
  },[state.user])  

  useEffect(() => {        
    start()
    fetchItems()    
  },[])  

  const userNameStyle = {
    margin: '1%',    
  }
  const imgStyle = {
    display: 'inline',
    width: '3%',
    height: '3%',
    margin: '5px'
  }

  const header = {
    display: 'flex',
    fontSize: '60%',
  }    

  const logo = {
    display: 'block',
    textAlign: 'left',
  }

  return (
    <div className="App">
      <AppBar>
        {/* <MakeReceipt /> */}
        <div style={logo}><HistoryApp tag='button_img' link='/' context={`${process.env.PUBLIC_URL}/header_logo.png`}/></div>        

        <span className='App-header' >
        { state.user && state.user.displayName ? 
        <>
          <Button onClick={logout}><LockIcon />ログアウト</Button>
          <img src={state.user.photoURL} style={imgStyle} alt="ユーザー"/>
          <span style={userNameStyle}>{state.user.displayName}</span>
        </>          
        :
        <Button onClick={login}><LockOpenIcon />ログイン</Button>
        }          
        <Button onClick={check}><CheckIcon />チェック</Button>        
        <HistoryApp tag='button' link='/cart' icon={<ShoppingCartIcon />} name='カート'/>
        <HistoryApp tag='button' link='/orderhistory' icon={<HistoryIcon />} name='注文履歴'/>                  
        </span> 
      </AppBar>     
            <div>                
                <Switch>
                    <Route path='/' exact component={ItemView} />
                    <Route path='/cart' exact component={Cart} />
                    <Route path='/itemdetail/:Item_id' exact component={ItemDetail} />
                    <Route path='/orderconfirm' exact component={OrderConfirm} />
                    <Route path='/orderfinish' exact component={OrderFinish} />
                    <Route path='/orderhistory' exact component={OrderHistory} />                                              
                    <Route>
                        <p>Not found</p>
                    </Route>
                </Switch>
            </div>
    </div>
  );
}

export default App;
