import React, {useState} from 'react'
import Button from '@material-ui/core/Button';
import SearchSharp from '@material-ui/icons/SearchSharp';
import BackspaceSharpIcon from '@material-ui/icons/BackspaceSharp';
import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom'

// import { GetItemById } from '../components/Items';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Card from '@material-ui/core/Card';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import '../App.css';
import { makeStyles } from '@material-ui/core/styles';
import ImageList from '@material-ui/core/ImageList';
import ImageListItem from '@material-ui/core/ImageListItem';
import ImageListItemBar from '@material-ui/core/ImageListItemBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

import { Switch, Route, Link } from 'react-router-dom';
import { useEffect } from 'react';
// import RactDom from 'react-dom'
const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    imageList: {
      width: "100%",
      height: "100%",
    },
    icon: {
      color: 'rgba(255, 255, 255, 0.54)',
    },
  }));

const ItemView =()=>{
    const state = useSelector(state => state)
    const [word, setWord]=useState('')      
    const [search, setSearch] = useState([])        
    const [searhResult, setSearchResult]  = useState('')

    const classes = useStyles();
    const history = useHistory()
    const handleLink = path => history.push(path)

    const type=(e)=>{
        setWord(e.target.value)
    }  
    const Kensaku=()=>{
        let newSearched = state.Coffee.filter(val2 =>{
            return val2.name.includes(word)
        });

        if(word === '' && newSearched.length === 0){
            newSearched = state.Coffee.concat()
        }

        setSearch(newSearched)
    }    

    const Kuria=()=>{
        const word=document.getElementById('box');
        word.value=''
        setWord('')
    }

    useEffect(()=>{                 
        setSearch(state.Coffee)                
    },[state.Coffee])

    useEffect(()=>{           
        if(search.length === 0){
            setSearchResult('検索結果がございませんでした')     
        }else{
            setSearchResult(search.length + '　件　商品がございます')
        }                             
    },[search])

    return(
    <Paper>
        <nav>
        <TableContainer>            
            <Table stickyHeader aria-label="sticky table" options={{
                    paging: false,                    
                    headerStyle: { position: 'sticky', top: 0 },
                }}>
                <TableHead>
                    <TableRow>
                        <TableCell>
                            <h3>商品を検索する</h3>                        
                            <input type='text' value={word} onChange={type}　id="box" />                        
                            <Button variant="contained" color="primary" type='submit' onClick={Kensaku}>検索<SearchSharp /></Button>
                            <Button variant="contained" color="primary" type='reset' onClick={Kuria}>クリア<BackspaceSharpIcon /></Button>
                        </TableCell>
                    </TableRow>                                
                </TableHead>
                <TableBody>
                    <div className={classes.root}>
                        <ImageList rowHeight={180} className={classes.imageList}>
                            <ImageListItem key="Subheader" cols={2} style={{ height: 'auto' }}>
                                <ListSubheader component="div">{searhResult}</ListSubheader>
                            </ImageListItem>                            
                            {search.map((val3,index)=>{
                                return(
                                    <ImageListItem key={index} onClick={()=>handleLink(`/itemdetail/${val3.id}`)}>
                                        <img src={`${process.env.PUBLIC_URL}/${val3.image}`} width="100%" alt="商品" />
                                        <ImageListItemBar
                                            title={val3.name}
                                            subtitle={
                                                <><p>【M】{val3.priceM}円(税抜)</p>
                                                <p>【L】{val3.priceL}円(税抜)</p></>}
                                            actionIcon={
                                                <IconButton aria-label={`info about ${val3.name}`} className={classes.icon}>
                                                    <InfoIcon />
                                                </IconButton>
                                            }
                                        />
                                    </ImageListItem>                                                                                
                                )
                            })}
                        </ImageList>
                    </div>
                </TableBody>                                    
            </Table>
        </TableContainer>
        </nav>
    </Paper>
    )}


export default ItemView
